function get_cookie(name)
{var find_sw=false;var start,end;for(var i=0;i<=document.cookie.length;i++)
{start=i;end=start+ name.length;if(document.cookie.substring(start,end)==name)
{find_sw=true;break}}
if(find_sw==true)
{start=end+ 1;end=document.cookie.indexOf(";",start);if(end<start)
end=document.cookie.length;return document.cookie.substring(start,end);}
return"";}
function set_cookie(name,value,days)
{var d=new Date();d.setTime(d.getTime()+(60*60*1000*days));document.cookie=name+"="+ value+"; path=/; expires="+ d.toUTCString()+";";}